import { crearFormularioMembresia } from './membresia';
import { renderTarjetaPropina } from './propina';
import { recompensas, contratos, puntos } from './data';

crearFormularioMembresia();
renderTarjetaPropina({ usuario: "Luis", artista: "DJ Luna", monto: 4.5 });

function renderListas() {
  const contenedor = document.createElement('div');

  contenedor.innerHTML = `<h2 class="text-xl mt-6 mb-2 font-semibold">Recompensas</h2>`;
  recompensas.forEach(r => {
    const div = document.createElement('div');
    div.className = "bg-white p-2 rounded mb-1";
    div.textContent = `${r.tipo}: ${r.descripcion}`;
    contenedor.appendChild(div);
  });

  contenedor.innerHTML += `<h2 class="text-xl mt-6 mb-2 font-semibold">Contratos</h2>`;
  contratos.forEach(c => {
    const div = document.createElement('div');
    div.className = "bg-white p-2 rounded mb-1";
    div.textContent = `${c.sello} - ${c.duracionMeses} meses`;
    contenedor.appendChild(div);
  });

  contenedor.innerHTML += `<h2 class="text-xl mt-6 mb-2 font-semibold">Sistema de Puntos</h2>`;
  puntos.forEach(p => {
    const div = document.createElement('div');
    div.className = "bg-white p-2 rounded mb-1";
    div.textContent = `${p.usuario}: ${p.puntos} puntos`;
    contenedor.appendChild(div);
  });

  document.body.appendChild(contenedor);
}

renderListas();
