export interface Propina {
  usuario: string;
  artista: string;
  monto: number;
}

export function renderTarjetaPropina(donacion: Propina) {
  const card = document.createElement('div');
  card.className = "bg-white p-4 rounded-lg shadow mb-4";

  card.innerHTML = `
    <h3 class="font-bold text-lg">${donacion.usuario} don√≥ a ${donacion.artista}</h3>
    <p>Monto: $${donacion.monto.toFixed(2)}</p>
  `;

  document.body.appendChild(card);
}
